mapper.update:
update sys_role set
del_flag = '2',
update_time=NOW(),
update_by=#{updateBy}
where id = #{id}

update sys_role set
role_name=#{roleName},
update_time=NOW(),
update_by=#{updateBy}
where id = #{id}

mapper.select:
select * from sys_role where del_flag=0

    AND id = #{id}


    AND role_name like concat('%',#{roleName},'%')
