mapper.update:
update sys_user set
real_name=#{realName},
phone=#{phone},
update_time=NOW(),
update_by=#{updateBy}
where id = #{id}

mapper.delete:
update sys_user set
del_flag = '2',
update_time=NOW(),
update_by=#{updateBy}
where id = #{id}

mapper.select:
select * from sys_user where del_flag=0

    AND id = #{id}


    AND user_name like concat('%',#{userName},'%')
